generated.css:
*{box-sizing: border-box;}
*:before,*:after{box-sizing: border-box;}
*{margin: 0;padding: 0;}
.icon {
            width: 1em;
            height: 1em;
            vertical-align: -0.15em;
            fill: currentColor;
            overflow: hidden;
        }
img{max-width: 100%; max-height: 100%;}
ul,ol{list-style: none;}

body{background-color: #eee;}

a{color: inherit;text-decoration: none;}

.globalHeader{
    margin:20px;
    display:flex;
    justify-content: space-between;
    
}

@media(min-width:500px){
    .globalHeader{
        margin: 60px 0 60px;
    }
}

.searchForm {
    margin:20px;
    display:flex;
    justify-content: space-between;
    max-width: 400px;
}

@media(min-width:500px){
    .searchForm {
        max-width: 400px;
        margin-left: auto ;
        margin-right: auto ;
    }   
}


.searchForm > input{
    width: 100%;
    margin-right: 10px;
    height:40px;
    padding: 0 10px;
    border: none;
    border:1px solid #ddd;
    border-radius: 4px;
}
.searchForm > button{
    white-space: nowrap;
    padding: 0 10px;
    border: none;
    border:1px solid #ddd;
    border-radius: 4px;
    width: 82px;
    height: 38px;
    left: 289px;
    top: 48px;
    background-color: #2D78E8;
    color: #fff;
    font-size: 20px;
}

.globalMain{
    max-width: 900px;
    margin-right: auto;
    margin-left: auto;
}

.siteList{
    
    margin: 20px;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
}

@media(min-width:500px){
    .siteList{
        margin-left: 0;
        margin-right: -25px;
        justify-content: flex-start;
    }
}
.siteList > li{
    margin-top: 10px; 
}

@media(min-width:500px){
    .siteList > li{
    margin-right: 25px;  
    }
}

.siteList .site{
    width:160px;
    display: flex;
    justify-self: center;
    flex-direction: column;
    align-items: center;
    background-color: white;
    border:1px solid #ddd;
    border-radius: 4px;
    padding: 20px 0;
    position: relative;
    cursor:pointer;
}
.siteList .site > .logo{
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 64px;
}
.siteList .site > link{
    font-size: 14px;
    margin-top: 4px;

}

.siteList .site > .close {
    position:absolute;
    right: 0;
    top: 0;
    display: none;
    cursor:default;
}

.siteList .site:hover > .close {
    display: block;
}

.siteList .addButton{
    width: 160px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    padding: 20px 0;
     background-color: white;
    border:1px solid #ddd;
    border-radius: 4px;
    height: 126.8px;
}

.siteList .addButton .icon{
    width: 56px;
    height: 56px;
}

.siteList .addButton .text{
    font-size: 14px;
    margin-top: 4px;
}


.siteList .addButton .icon-wrapper{
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
}
